	public void sendMessage(View view) {

		MyApplication app = (MyApplication) getApplication();
		
		// Get the message from the box
		//EditText et = (EditText) findViewById(R.id.MessageText);
		//String msg = et.getText().toString();

		// Create an array of bytes.  First byte will be the
		// message length, and the next ones will be the message
		
		
		//byte buf[] = new byte[msg.length() + 1];
		//buf[0] = (byte) msg.length(); 
		//System.arraycopy(msg.getBytes(), 0, buf, 1, msg.length());
		
		//first four bytes are length of data block 
		int size = pic_array.length*2;
		byte buf[] = new byte[size+4];
		
		buf[0] = (byte)((size & 0xff000000) >> 24);
		buf[1] = (byte)((size & 0x00ff0000) >> 16);
		buf[2] = (byte)((size & 0x0000ff00) >> 8);
		buf[3] = (byte)(size & 0x000000ff);
		
		int i;
		for(i=0; i<pic_array.length; i=i+1){
	         buf[2*i+4] = (byte)((pic_array[i] & 0x0000ff00) >> 8);
	         buf[2*i+5] = (byte)(pic_array[i] & 0x000000ff);
		}

		// Now send through the output stream of the socket
		
		OutputStream out;
		try {
			out = app.sock.getOutputStream();
			try {
				out.write(buf, 0, buf.length);
			} catch (IOException e) {
				e.printStackTrace();
			}
		} catch (IOException e) {
			e.printStackTrace();
		}
	}
	
	
	// Route called when the user presses "connect"
		public void openSocket(View view) {
			MyApplication app = (MyApplication) getApplication();
			
			TextView msgbox = (TextView) findViewById(R.id.error_message_box);

			// Make sure the socket is not already opened 
			if (app.sock != null && app.sock.isConnected() && !app.sock.isClosed()) {
				msgbox.setText("Socket already open");
				return;
			}
			else{
				msgbox.setText("Socket is opened");
			}
			// open the socket.  SocketConnect is a new subclass
		    // (defined below).  This creates an instance of the subclass
			// and executes the code in it.
			new SocketConnect().execute((Void) null);
		}
		
		
		// Called when the user closes a socket
		public void closeSocket(View view) {
			MyApplication app = (MyApplication) getApplication();
			Socket s = app.sock;
			TextView msgbox = (TextView) findViewById(R.id.error_message_box);
			try {
				s.getOutputStream().close();
				s.close();
				msgbox.setText("Socket has been closed");
			} catch (IOException e) {
				e.printStackTrace();
			}
		}
		
